				GROUP BY se.SectorName
			) AS fvr
			GROUP BY fvr.Name
			ORDER BY fvr.Name;
			
			DROP TEMPORARY TABLE IF EXISTS SchoolDataBySector;				 
		END;
		END IF;   
	END;
	
