                SELECT 1 AS Id, CAST(SUM(cs.Total) AS CHAR) AS Name, SUM(cs.Class9) AS Class9, SUM(cs.Class10) AS Class10, SUM(cs.Class11) AS Class11, SUM(cs.Class12) AS Class12, SUM(cs.Total) AS Total
