		GROUP BY cm.VTPId
		ORDER BY vtp.VTPShortName;
	END;
    ELSEIF (DataType = 'BySchoolManagement') THEN
	BEGIN
		SELECT ROW_NUMBER() OVER (ORDER BY dvv.Name ASC) AS Id, 
