	# CALL GetDashboardGuestLectureChartDataV2 ('ByTimeline', 'rakesh.gtmcs@gmail.com', 'fda59df6-729e-493c-8d48-fc65b90008fe', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL);
