				FROM (				 
					SELECT dvv.Name, 
						sa.ReportMonth, 
                        sa.TotalDays, 
						(DAY(LAST_DAY(sa.ReportMonth)) - GetNoOfSunday(sa.ReportMonth)) AS TotalWorkingDays, 
						SUM(sa.Boys) AS Boys, SUM(sa.Girls) AS Girls, 
						SUM(sa.AttendanceBoys) AS AttendanceBoys, SUM(sa.AttendanceGirls) AS AttendanceGirls						
