			END;
		ELSEIF(DataType = 'BySector') THEN
			BEGIN  
				SELECT ROW_NUMBER() OVER (ORDER BY s.SectorName ASC) AS Id, s.SectorName AS Name, SUM(sd.Boys) AS Boys, SUM(sd.Girls) AS Girls, SUM(sd.Total) AS Total
