    )
BEGIN
	SET SESSION TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;
    
    DROP TEMPORARY TABLE IF EXISTS DistrictTmp;
    DROP TEMPORARY TABLE IF EXISTS tblDistricts;
    
	CREATE TEMPORARY TABLE DistrictTmp(DistrictId TEXT);    
