                IF(ctr.VCAttendance IS NOT NULL, CAST(ctr.VCAttendance AS DECIMAL(10, 2)), 0) AS Percentage 
			FROM (
				SELECT DISTINCT dca.VTPId, 
					dca.ReportingMonth,
