			AND (ClassId IS NULL OR vtc.ClassId = ClassId)
			AND (SchoolManagementId IS NULL OR s.SchoolManagementId = SchoolManagementId)
        GROUP BY s.DivisionId, s.DistrictCode, vss.SectorId, vt.VTPId, s.SchoolManagementId;
		 
