BEGIN
	SET SESSION TRANSACTION ISOLATION LEVEL READ UNCOMMITTED ;
    
	SELECT r.Code INTO @roleName
	FROM AccountRoles ar 
	INNER JOIN Accounts a ON ar.AccountId = a.AccountId AND a.IsActive = 1
