			ex.WillContHigherStudies,
			dvw.Name AS WillBeFullTime,
			dvcp.Name AS CourseToPursue,
			dved.Name AS StreamOfEducation,
			ex.WillContVocEdu,
			dvrc.Name AS ReasonsNOTToContinue,
