		ex.ParentMobileNo,
		ex.ParentName,
		ex.DoneInternship,
		ex.CurrentlyEmployed,
		ex.DetailsOfEmployment,
		dvf.Name AS IsFullTime,
		dvse.Name AS SectorsOfEmployment,
		ex.IsRelevantToVocCourse,
		ex.WillContHigherStudies,
		dvw.Name AS WillBeFullTime,
		dvcp.Name AS CourseToPursue,
