		CROSS JOIN (SELECT @rowIndex := 0) AS DataRows    		 
        ORDER BY ay.YearName, ph.PhaseName, vtp.VTPName, vc.FullName, vt.FullName, glc.ReportingDate DESC;
	
		DROP TEMPORARY TABLE IF EXISTS VTGuestLectureConductedData;
	END;
	
