		ORDER BY ay.YearName, ph.PhaseName, SchoolAllottedYear, vtp.VTPName, vc.FullName, vt.FullName, se.SectorName, s.SchoolName;
    END;

	DROP TEMPORARY TABLE IF EXISTS DistrictTmp;
