        sec.SectorName AS Sector, 
		jr.JobRoleName AS JobRole, 
        s.SchoolName AS NameOfSchool, 
		s.Udise AS UdiseCode, 
		s.DistrictCode AS District, 
		scl.Name AS Class, 
