		INNER JOIN VTSchoolSectors vtss ON vsv.VTId = vtss.VTId AND vsv.SchoolId = vtss.SchoolId AND vsv.SectorId = vtss.SectorId AND vsv.JobRoleId = vtss.JobRoleId 	
