		AND ((charBy IS NULL OR charBy = 'All') OR (vt.FullName LIKE CONCAT(charBy, '%')))
	ORDER BY vtp.VTPShortName, vc.FullName, vt.FullName
	LIMIT pageSize OFFSET offSetValue;

