    WHERE vda.ReportingDate BETWEEN startDate AND endDate
    GROUP BY vt.FullName
	ORDER BY vc.FullName, vt.FullName
    LIMIT 3;
	
    SET SESSION TRANSACTION ISOLATION LEVEL REPEATABLE READ;

    
END ;;
