	SELECT AcademicYearId, YearName INTO @AcademicYearId, @YearName
    FROM AcademicYears 
    WHERE CAST(CONCAT(YEAR(NOW()),'/10/01') AS DATE) BETWEEN StartMonth AND EndMonth;
	
